import { Box, Stack } from '@mui/material';
import React from 'react';
import { Link } from 'react-router-dom';
import { useState, useEffect } from "react";

export default (props) => {
    const subject = props.subject;

    const ref = useRef(null);
    
    const [content, setContent] = useState(subject.Description);
    const [background, setBackground] = useState("#00000030");
    const [scrollPos, setScrollPos] = useState(ref.current.scrollTop);

    return (
        <>
        <Stack onClick={()=>{setContent(subject.Body.replace('${description}',subject.Description))}}
                onMouseLeave={()=>{setContent(subject.Description);setBackground("#00000030");window.scrollTop({top:ref.current.offsetTop,behaviour:"smooth"})}}
                onMouseEnter={()=>{setBackground("#00000045"); }}
                direction={'column'} width={'80vw'} alignItems={'center'} sx={{border: '0.1vw solid black', background: background, padding: '0 4vw'}}>
            <h3>{subject.Title}</h3>
            <h4><b>Class: </b>{subject.Class}</h4><br/>
            <p>{content}</p>
        </Stack>
        </>
    )
}
