import { Box, Stack } from '@mui/material';
import React from 'react';
import { Link } from 'react-router-dom';
import { useState, useEffect } from "react";

export default (props) => {
    const subject = props.subject;
    
    const [content, setContent] = useState(subject.Description);

    return (
        <>
        <Stack onClick={()=>{setContent(subject.Body.replace('${description}',subject.Description))}}
                onMouseLeave={()=>{setContent(subject.Description);setBackground("#00000030")}} direction={'column'} width={'80vw'} alignItems={'center'} sx={{border: '0.1vw solid black', padding: '0 4vw'}}>
            <h3>{subject.Title}</h3>
            <h4><b>Class: </b>{subject.Class}</h4><br/>
            <p>{content}</p>
        </Stack>
        </>
    )
}
