export default (props) => {
    const subject = props.subject;
    
    const [subjects, setData] = useState(null);
    return (
        <>
        <Stack onClick={()=>{console.log(this)}} direction={'column'} width={'80vw'} alignItems={'center'} sx={{border: '0.1vw solid black', padding: '0 4vw'}}>
            <h3>{subject.Title}</h3>
            <h4><b>Class: </b>{subject.Class}</h4><br/>
            <p>{false?subject.Body.replace('${description}',subject.Description):subject.Description}</p>
        </Stack>
        </>
    )
}
